import styled from 'styled-components';

// Estilos para a página de vendas
export const SalePageContainer = styled.div`
  text-align: center;
  font-family: Arial, sans-serif;
  padding-top: 60px; // Adicionado para empurrar o conteúdo para baixo
`;

export const SaleHeader = styled.header`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: black;
  color: white;
  padding: 20px 0;
  width: 100%;
  position: fixed;
  top: 0;
  z-index: 1000;
`;

export const SaleNav = styled.nav`
  display: flex;
  justify-content: center;
  gap: 20px; // Aumenta o espaçamento entre os itens
`;

export const SaleNavItem = styled.a`
  color: white;
  text-decoration: none;
  font-size: 16px;
  cursor: pointer;

  &:hover {
    color: #ccc; // Muda a cor em vez de sublinhar
  }
`;

export const SaleTitle = styled.h1`
  font-size: 24px;
  margin-bottom: 10px;
`;

export const MainBanner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px 20px;
  background-color: white;
  color: black;
  margin-top: 100px;
`;

export const BannerTitle = styled.h2`
  font-size: 24px;
  margin-bottom: 5px;
`;

export const BannerSubtitle = styled.p`
  font-size: 16px;
  margin-bottom: 15px;
`;

export const BannerButton = styled.button`
  background-color: green;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 4px;
  font-size: 18px;
  cursor: pointer;

  &:hover {
    background-color: darkgreen;
  }
`;

export const ProductsContainer = styled.div`
  display: flex;
  justify-content: center;
  gap: 20px; // Aumenta o espaçamento entre os produtos
  margin-top: 20px;
`;

export const Product = styled.div`
  margin: 0;
  text-align: center;
`;

export const ProductImage = styled.img`
  width: 150px; // Ajuste conforme necessário para as imagens
  height: auto;
  margin-bottom: 5px;
`;

export const ProductButton = styled.button`
  background-color: black;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;

  &:hover {
    background-color: gray;
  }
`;
