import styled from "styled-components";

export const Item = styled.div`
  margin-bottom: 20px;
  padding: 2%;
  border: 1px solid #ccc;
  border-radius: 10px;
  outline: none;
  background-color: white;
  box-shadow: 7px 10px 13px 2px rgba(0, 0, 0, 0.1); // Mantenha apenas esta declaração
  transition: transform 0.3s ease-in-out, box-shadow 0.3s ease-in-out, background-color 0.3s ease-in-out;
  display: flex;
  flex-direction: column;
  align-items: center; // Centralize para melhor simetria
  justify-content: center;
  text-align: center;
  height: 310px;
  width: 80%;
  max-width: 300px;
  margin: 10px 20px;
  &:hover {
    background-color: #f7f7f7;
    cursor: pointer;
    transform: translateY(-5px);
    box-shadow: 0 6px 16px rgba(0, 0, 0, 0.15);
  }
  h1 {
    font-size: 1.1rem;
    text-transform: uppercase;
    margin: 20px 5px 5px 0px;
    font-weight: 600;
  }
  h2 {
    font-size: 1em;
    color: green;
    font-weight: 200;
    margin: 0px;
  }
  p {
    font-size: 1rem;
  }
`;

export const Info = styled.div`
  width: 100%; // Expande para preencher o espaço horizontal disponível
  height: auto; // Ajuste baseado no conteúdo
  padding: 2px;
`;

export const ListaFeed = styled.div`
  display: flex;
  justify-content: center;
  flex-wrap: wrap; // Permite que os itens se envolvam conforme necessário
  gap: 20px; // Consistente espaçamento entre itens
`;

export const FotoProduto = styled.img`
  background-color: grey;
  border-radius: 6px;
  width: 100%;
  height: auto; // Mantenha a proporção da imagem
  object-fit: cover; // Garante que a imagem cubra o espaço designado sem distorcer
  margin: 0px;
  padding: 0px;
`;
