import styled from 'styled-components';

// Estilos para a página de vendas
export const SalePageContainer = styled.div`
  text-align: center;
  font-family: Arial, sans-serif;
`;

export const SaleHeader = styled.header`
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: black;
  color: white;
  padding: 40px 0;
  width: 100%;
  position: fixed;
  top: 0;
  z-index: 1000;
`;

export const SaleNav = styled.nav`
  display: flex;
  justify-content: center;
  gap: 40px; // Ajuste conforme necessário para corresponder ao espaçamento na imagem
`;

export const SaleNavItem = styled.a`
  color: white;
  text-decoration: none;
  padding: 0 20px;
  font-size: 20px;
  cursor: pointer;

  &:hover {
    color: lightgray; // Altera cor em vez de sublinhado para hover
  }
`;

export const SaleTitle = styled.h1`
  font-size: 36px; // Mantido conforme o original
  margin: 0 0 20px; // Ajustado para alinhamento vertical
`;

export const MainBanner = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 60px 20px;
  background-color: white;
  color: black;
  margin-top: 140px; // Ajuste para o padding-top do header
`;

export const BannerTitle = styled.h2`
  font-size: 36px;
  margin-bottom: 10px;
`;

export const BannerSubtitle = styled.p`
  font-size: 18px;
  margin-bottom: 20px;
`;

export const BannerButton = styled.button`
  background-color: green;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 5px;
  font-size: 20px;
  cursor: pointer;

  &:hover {
    background-color: darkgreen;
  }
`;

export const ProductsContainer = styled.div`
  display: flex;
  justify-content: center;
  margin-top: 20px;
`;

export const Product = styled.div`
  margin: 0 15px;
  text-align: center;
`;

export const ProductImage = styled.img`
  width: 100px; // Ajuste conforme necessário para corresponder ao tamanho da imagem
  height: auto;
  margin-bottom: 10px;
`;

export const ProductButton = styled.button`
  background-color: black;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 16px;

  &:hover {
    background-color: gray;
  }
`;
