import styled from "styled-components";

export const Item = styled.div`
  margin-bottom: 20px; // Mantém o espaçamento entre os itens
  padding: 2%; // Espaçamento interno para não tocar diretamente nas bordas
  border: 1px solid #ccc; // Uma cor mais suave para a borda
  border-radius: 10px; // Bordas mais arredondadas para um visual moderno
  outline: none; // Remove o contorno padrão que alguns navegadores aplicam
  background-color: white; // Fundo branco para fazer o conteúdo se destacar
  box-shadow: 7px 10px 13px 2px rgba(0, 0, 0, 0.1);
  &:hover {
    background-color: #f7f7f7;
    cursor: pointer;
    transform: translateY(
      -5px
    ); // Levanta ligeiramente o item ao passar o mouse
    box-shadow: 0 6px 16px rgba(0, 0, 0, 0.15); // Sombra mais proeminente no hover
  }

  display: flex; // Usa flexbox para organizar o conteúdo internamente
  flex-direction: column;
  align-items: flex-start; // Centraliza os itens no eixo cruzado
  justify-content: center; // Centraliza os itens no eixo principal
  text-align: center; // Textos centralizados
  height: 310px;
  width: 80%; // Largura total para ocupar o espaço disponível
  max-width: 250px; // Máximo de largura para evitar que fique muito grande
  margin: 10px 20px;
  h1 {
    font-size: 1.1rem;
    text-transform: uppercase;
    margin: 20px 5px 5px 0px;
    font-weight: 600;
  }
  h2 {
    font-size: 1em;
    color: green;
    font-weight: 200;
    margin: 0px;
  }
  p {
    font-size: 1rem;
  }
`;

export const Info = styled.div`
  width: 200px;
  height: 100px;
  padding: 2px;
`;

export const ListaFeed = styled.div`
  display: flex;
  justify-content: center;
  /* background-color: blue; */
`;
export const FotoProduto = styled.img`
  background-color: grey;
  border-radius: 6px;
  width: 100%;
  height: 100%;
  margin: 0px;
  padding: 0px;
`;
