import React, { useState } from "react";
import * as S from "../../App.styles";
import * as B from "./style";
import { createUserWithEmailAndPassword } from "firebase/auth";
import { auth } from "../../firebase-config"; // Ajuste o caminho conforme o local do seu arquivo de configuração do Firebase


const FormsRegister = () => {
  const [fullName, setFullName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  return (
    <S.Form>
      <B.p>Esta é minha primeira compra!</B.p>
      <S.Input
        type="text"
        placeholder="Digite seu nome completo"
        value={fullName}
        onChange={(e) => setFullName(e.target.value)}
      />
      <S.Input
        type="email"
        placeholder="Digite seu e-mail"
        value={email}
        onChange={(e) => setEmail(e.target.value)}
      />
      <S.Input
        type="password"
        placeholder="Digite sua senha"
        value={password}
        onChange={(e) => setPassword(e.target.value)}
      />
      <S.Button>Cadastre-se</S.Button>
    </S.Form>
  );
};

export default FormsRegister;
