import styled from "styled-components";

export const ContainerLogin = styled.div`
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    height: 100vh;
    background-color: #ffffff;  // Background branco limpo
`;

export const HeaderLogin = styled.header`
    background-color: black;
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 20px 10%;
    width: 100%;
    box-sizing: border-box;
`;

export const Title = styled.h1`
    color: white;
    font-size: 24px;  // Ajustar conforme a fonte na imagem
    margin: 0;
`;

export const Subtitle = styled.div`
    color: white;
    font-size: 16px;
    display: flex;
    align-items: center;
    gap: 10px;
`;

export const FormsContainer = styled.div`
    display: flex;
    justify-content: center;
    align-items: center;
    margin-top: -50px;  // Eleva os formulários para cima para o ajuste visual
`;

export const Form = styled.div`
    background-color: #FFFFFF;
    padding: 40px;
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
    border-radius: 8px;
    width: 300px;
    display: flex;
    flex-direction: column;
    align-items: center;
    margin: 0 20px;  // Espaço entre os formulários
`;

export const Input = styled.input`
    width: 100%;
    padding: 10px;
    margin: 10px 0;
    border: 1px solid #ccc;
    border-radius: 4px;
`;

export const Button = styled.button`
    width: 100%;
    padding: 10px 20px;
    background-color: black;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
    font-size: 16px;
    margin-top: 20px;  // Espaço antes do botão
`;
