sourcesContent:
import styled from 'styled-components';

// Estilos para a página de login
export const SalePageContainer = styled.div`
  text-align: center;
  font-family: Arial, sans-serif;
`;

export const SaleHeader = styled.header`
  display: flex;
  flex-direction: row; // Alterado para linha para melhor alinhamento
  justify-content: space-between; // Espaçamento entre itens de navegação
  background-color: black;
  color: white;
  padding: 20px 40px; // Ajuste no padding para menos espaço
  width: 100%;
  position: fixed;
  top: 0;
  z-index: 1000;
`;

export const SaleNav = styled.nav`
  display: flex;
`;

export const SaleNavItem = styled.a`
  color: white;
  text-decoration: none;
  margin: 0 20px;
  font-size: 20px;
  cursor: pointer;
  &:hover {
    color: lightgray;
  }
`;

export const SaleTitle = styled.h1`
  font-size: 24px; // Reduzido para melhor ajuste no cabeçalho
`;

export const MainSection = styled.div`
  display: flex;
  justify-content: space-between; // Espaçamento entre as seções
  align-items: start;
  padding-top: 80px; // Ajuste para a altura do header
  margin: 20px 40px; // Margem externa para alinhamento
`;

export const MainBanner = styled.div`
  flex: 1;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

export const BannerTitle = styled.h2`
  font-size: 28px; // Tamanho reduzido para balanceamento
`;

export const BannerSubtitle = styled.p`
  font-size: 18px;
`;

export const BannerButton = styled.button`
  padding: 10px 20px;
  background-color: green;
  color: white;
  font-size: 18px;
  border-radius: 5px;
  cursor: pointer;
  &:hover {
    background-color: darkgreen;
  }
`;

export const ProductsContainer = styled.div`
  flex: 2; // Mais espaço para produtos
  display: flex;
  justify-content: space-around; // Distribuição igual dos produtos
`;

export const Product = styled.div`
  text-align: center;
`;

export const ProductImage = styled.img`
  width: 160px; // Tamanho maior para destaque
  height: auto;
`;

export const ProductButton = styled.button`
  padding: 8px 16px;
  background-color: black;
  color: white;
  font-size: 14px;
  border-radius: 4px;
  cursor: pointer;
  &:hover {
    background-color: gray;
  }
`;
