import styled from "styled-components";

export const Foto = styled.img`
    background-color: blue;
  img{
    width: 100%;        // Faz a imagem cobrir totalmente o slide
    height: auto;       // Mantém a proporção da imagem, altere se necessário
    background-color: blue;
    /* vertical-align: middle; // Ajuda a alinhar a imagem verticalmente */
  }
`;
